// export default App;

// ---Recreating mini project using component---

function App() {
  const word = "Hello Sir";
  let date = new Date(2023, 5, 1, 1);
  const hr = date.getHours();
  let greet = "";
  const cssStyle = {};
  if (hr >= 1 && hr <= 11) {
    greet = "Good Morning";
    cssStyle.color = "green";
  } else if (hr >= 12 && hr <= 19) {
    greet = "Good Afternoon";
    cssStyle.color = "orange";
  } else {
    greet = "Good Night";
    cssStyle.color = "black";
  }
}
